import {SchoolService} from "./schoolService";
import {ApplicationService} from "./applicationService";
import {ProfileService} from "./profileService";
import {CandidateService} from "./candidateService";
import {Profile} from "../dto/profile";
import {Application} from "../dto/application";
import {SchoolWithProfiles} from "../dto/schoolWithProfiles";
import {Grade} from "../dto/grade";
import {ITask} from "pg-promise";
import {ProfileCriteriaEntity, ProfileCriteriaType} from "../models/profileCriteriaEntity";
import {ApplicationStatus} from "../dto/applicationStatus";
import {ResourceNotFoundError} from "../errors/resourceNotFoundError";

export class AdminService {
    constructor(
        private schoolService: SchoolService,
        private candidateService: CandidateService,
        private profileService: ProfileService,
        private applicationService: ApplicationService,
        private readonly tx: (callback: (t: ITask<any>) => Promise<void>) => Promise<void>
    ) {
    }

    async enroll(): Promise<void> {
        const profiles: Profile[] = await this.profileService.getAllProfiles();
        const profilesCriteria: Map<number, ProfileCriteriaEntity[]> = await this.profileService.getAllProfilesCriteria();

        const maxPriority: number = await this.applicationService.getMaxPriority();
        const gradesByCandidate = await this.candidateService.getAllWithGrades();

        await this.tx(async t => {
            for (let priority = 1; priority < maxPriority; priority++) {
                for (let profile of profiles) {
                    const profileCriteria = profilesCriteria.get(profile.id);
                    if (!profileCriteria) throw new ResourceNotFoundError('Criteria not found');
                    await this.processEnrollmentForProfile(profile, profileCriteria, priority, gradesByCandidate, t);
                }
            }
        });
    }

    private async processEnrollmentForProfile(
        profile: Profile,
        criteria: ProfileCriteriaEntity[],
        priority: number,
        gradesByCandidate: Map<number, Grade[]>,
        t: ITask<any>
    ): Promise<void> {
        const capacity: number = profile.capacity;
        let enrolledNumber: number = await this.applicationService.getAllEnrolledByProfile(profile.id);
        let enrolledCandidateIds: number[] = [];

        let applications: Application[] = (await this.applicationService.getAllPendingApplicationsByProfileAndPriority(profile.id, priority))
            .sort((a, b) =>
                this.calculatePoints(criteria, gradesByCandidate.get(a.candidateId)!)
                - this.calculatePoints(criteria, gradesByCandidate.get(b.candidateId)!)
            );
        while (enrolledNumber < capacity && applications.length > 0) {
            enrolledCandidateIds.push(applications.pop()!.candidateId);
            enrolledNumber = enrolledNumber + 1;
        }

        const rejectedApplicationIds = applications.map(app => app.id);
        await this.updateApplicationStatuses(enrolledCandidateIds, rejectedApplicationIds, profile.id, t);
    }

    private async updateApplicationStatuses(enrolled: number[], rejected: number[], profileId: number, t: ITask<any>) {
        for (let candidateId of enrolled) {
            const applications = await this.applicationService.getAllApplications(candidateId);
            for (let application of applications) {
                await this.applicationService.updateApplicationStatus(
                    application.id,
                    (application.profile.id === profileId)
                        ? ApplicationStatus.Accepted
                        : ApplicationStatus.Rejected,
                    t
                );
            }
        }

        for (let applicationId of rejected) {
            await this.applicationService.updateApplicationStatus(applicationId, ApplicationStatus.Rejected, t);
        }

    }

    private calculatePoints(profileCriteria: ProfileCriteriaEntity[], grades: Grade[]) {
        const mandatorySubjects = profileCriteria.filter(s => s.type === ProfileCriteriaType.Mandatory);
        const alternativeSubjects = profileCriteria.filter(s => s.type === ProfileCriteriaType.Alternative);
        let points = 0;

        let alternativeGrades = []
        for (let grade of grades) {
            if (grade.type == "exam") {
                points += grade.grade;
            } else if (grade.subjectId in mandatorySubjects) {
                points += grade.grade;
            } else if (grade.subjectId in alternativeSubjects) {
                alternativeGrades.push(grade.grade);
            }
        }
        return points + alternativeGrades.sort().pop()!;
    }
}

export enum ProfileCriteriaType {
    Mandatory = 'mandatory',
    Alternative = 'alternative',
}

export interface ProfileCriteriaEntity {
    id: number;
    profileId: number;
    subjectId: number;
    type: ProfileCriteriaType;
}

export enum ApplicationStatus {
    Pending = 'pending',
    Accepted = 'accepted',
    Rejected = 'rejected',
}

import {CustomError} from "./customError";

export class ResourceNotFoundError extends CustomError {
    constructor(message: string = 'Data does not exist') {
        super(message, 404);
    }
}

export class CustomError extends Error {
    public statusCode: number;
    public message: string;
    public isOperational: boolean;

    constructor(message: string, statusCode: number, isOperational: boolean = true) {
        super(message);
        this.statusCode = statusCode;
        this.message = message;
        this.isOperational = isOperational;

        Error.captureStackTrace(this, this.constructor);
    }
}

import {ProfileRepository} from "../repositories/profileRepository";
import {ProfileCriteriaEntity} from "../models/profileCriteriaEntity";
import {Profile} from "../dto/profile";
import {ResourceNotFoundError} from "../errors/resourceNotFoundError";

export class ProfileService {
    constructor(private profileRepository: ProfileRepository) {}

    async getProfile(profileId: number): Promise<Profile> {
        const profile = await this.profileRepository.getById(profileId);
        if (!profile) throw new ResourceNotFoundError('Profile not found.');
        return profile;
    }

    getProfilesBySchool(schoolId: number): Promise<Profile[]> {
        return this.profileRepository.getAllBySchool(schoolId);
    }

    async getAllProfiles(): Promise<Profile[]> {
        return this.profileRepository.getAll();
    }

    async getAllProfilesCriteria(): Promise< Map<number, ProfileCriteriaEntity[]>> {
        const allCriteria: ProfileCriteriaEntity[] = await this.profileRepository.getAllProfilesCriteria();

        const profilesCriteriaMap = new Map<number, ProfileCriteriaEntity[]>();

        allCriteria.forEach(criteria => {
            if (!profilesCriteriaMap.has(criteria.profileId)) {
                profilesCriteriaMap.set(criteria.profileId, []);
            }
            profilesCriteriaMap.get(criteria.profileId)?.push(criteria);
        });

        return profilesCriteriaMap;
    }
}

import {CandidateRepository} from "../repositories/candidateRepository";
import {DataConflictError} from "../errors/dataConflictError";
import {ResourceNotFoundError} from "../errors/resourceNotFoundError";
import {GradeService} from "./gradeService";
import {Grade} from "../dto/grade";
import {Candidate} from "../dto/candidate";
import {CandidateEntity} from "../models/candidateEntity";
import {CandidateRequest} from "../dto/candidateRequest";

export class CandidateService {
    constructor(
        private readonly gradeService: GradeService,
        private readonly candidateRepository: CandidateRepository) {
    }

    async getAllWithGrades() {
        const gradesByCandidateId = new Map<number, Grade[]>;
        const candidates: Candidate[] = await this.candidateRepository.getAll();
        for (const candidate of candidates) {
            gradesByCandidateId.set(candidate.id, await this.gradeService.getAllByCandidate(candidate.id));
        }
        return gradesByCandidateId;
    }

    async getLastCreatedCandidateByUser(userId: number) {
        return this.candidateRepository.getLastUpdatedByUser(userId);
    }

    async getCandidate(id: number, userId: number) {
        const candidate: Candidate | null = await this.candidateRepository.getById(id);
        if (candidate == null || candidate.userId != userId) {
            throw new ResourceNotFoundError('Candidate not found.');
        }
        return candidate;
    }

    async deleteCandidate(id: number, userId: number) {
        await this.getCandidate(id, userId);
        await this.candidateRepository.deleteById(id);
    }

    async getAllByUser(userId: number): Promise<Candidate[]> {
        return this.candidateRepository.getAllByUser(userId);
    }

    async register(userId: number, candidate: CandidateRequest): Promise<Candidate> {
        const existingCandidate: Candidate | null = await this.candidateRepository.getByPesel(candidate.pesel);
        if (existingCandidate) {
            throw new DataConflictError('There is already candidate with that pesel.');
        }

        const newCandidate: CandidateEntity = {
            id: 0,
            userId,
            firstName: candidate.firstName,
            lastName: candidate.lastName,
            pesel: candidate.pesel,
        };

        return await this.candidateRepository.insert(newCandidate);
    }
}

import {CustomError} from "./customError";

export class DataConflictError extends CustomError {
    constructor(message: string = 'Data already exists') {
        super(message, 409);
    }
}

import {ITask} from "pg-promise";

import {ApplicationRepository} from '../repositories/applicationRepository';
import {ResourceNotFoundError} from "../errors/resourceNotFoundError";
import {DataConflictError} from "../errors/dataConflictError";
import {SchoolService} from "./schoolService";
import {ProfileService} from "./profileService";
import {ApplicationWithProfiles} from "../dto/applicationWithProfiles";
import {ApplicationBySchool} from "../dto/applicationBySchool";
import {ApplicationRequest} from "../dto/applicationRequest";
import {ApplicationEntity} from "../models/applicationEntity";
import {Application} from "../dto/application";
import {EnrollmentService} from "./enrollmentService";
import {ValidationError} from "../errors/validationError";
import {Enrollment} from "../dto/enrollment";
import {ApplicationStatus} from "../dto/applicationStatus";

export class ApplicationService {
    constructor(
        private applicationRepository: ApplicationRepository,
        private profileService: ProfileService,
        private enrollmentService: EnrollmentService,
        private schoolService: SchoolService,
        private readonly tx: (callback: (t: ITask<any>) => Promise<void>) => Promise<void>
    ) {
    }

    async getAllApplications(candidateId: number): Promise<ApplicationWithProfiles[]> {
        const applications = await this.applicationRepository.getAllByCandidate(candidateId);

        return Promise.all(
            applications.map(async (app) => {
                const [enrollment, profile, school] = await Promise.all([
                    this.enrollmentService.getEnrollment(app.enrollmentId),
                    this.profileService.getProfile(app.profileId),
                    this.schoolService.getSchoolWithProfiles(app.profileId)
                ]);

                return {
                    id: app.id,
                    school: school,
                    profile: profile,
                    priority: app.priority,
                    round: enrollment.round,
                    status: app.status,
                    createdAt: app.createdAt,
                    updatedAt: app.updatedAt
                };
            })
        )
    }

    async getAllPendingApplicationsByProfileAndPriority(profileId: number, priority: number): Promise<Application[]> {
        return this.applicationRepository.getAllPendingByProfileAndPriority(profileId, priority);
    }

    async getAllApplicationSubmissions(candidateId: number): Promise<ApplicationBySchool[]> {
        const applications: ApplicationWithProfiles[] = await this.getAllApplications(candidateId);
        return this.groupApplicationsBySchool(applications);
    }

    async getAllEnrolledByProfile(profileId: number): Promise<number> {
        return this.applicationRepository.getEnrolledByProfile(profileId)
    }

    async getMaxPriority(): Promise<number> {
        return this.applicationRepository.getMaxPriority();
    }

    async updateApplicationStatus(id: number, status: string, t: ITask<any>) {
        return this.applicationRepository.updateStatus(id, status, t);
    }

    async addApplication(submissions: ApplicationRequest[], candidateId: number): Promise<void> {
        const enrollment: Enrollment | null = await this.enrollmentService.getCurrentEnrollment();
        if (!enrollment) throw new ValidationError('Outside the enrollment period.');

        const existingApplications: Application[] = await this.applicationRepository.getAllByCandidateAndEnrollmentId(candidateId, enrollment.id);
        if (existingApplications.length > 0) throw new DataConflictError('Application already exists');

        await this.validateProfilesExist(submissions);
        this.assignPriorities(submissions);

        await this.tx(async t => {
            for (const submission of submissions) {
                const newApplication = this.createApplicationEntity(submission, candidateId, enrollment.id);
                await this.applicationRepository.insert(newApplication, t);
            }
        });
    }

    async updateApplication(submissions: ApplicationRequest[], candidateId: number): Promise<void> {
        const enrollment: Enrollment | null = await this.enrollmentService.getCurrentEnrollment();
        if (!enrollment) throw new ValidationError('Outside the enrollment period.');

        const applications: Application[] = await this.applicationRepository.getAllByCandidateAndEnrollmentId(candidateId, enrollment.id);
        if (applications.length === 0) throw new ResourceNotFoundError('Applications not found.');

        await this.validateProfilesExist(submissions);
        this.assignPriorities(submissions);

        await this.tx(async t => {
            for (const application of applications) {
                await this.applicationRepository.delete(application.profileId, application.candidateId, t);
            }
            for (const submission of submissions) {
                const newApplication = this.createApplicationEntity(submission, candidateId, enrollment.id);
                await this.applicationRepository.insert(newApplication, t);
            }
        });
    }

    private createApplicationEntity(submission: ApplicationRequest, candidateId: number, enrollmentId: number): ApplicationEntity {
        return {
            id: 0,
            candidateId,
            profileId: submission.profileId,
            priority: submission.priority,
            enrollmentId,
            status: ApplicationStatus.Pending,
            createdAt: new Date(),
            updatedAt: new Date(),
        };
    }

    private async validateProfilesExist(submissions: ApplicationRequest[]) {
        for (const submission of submissions) {
            await this.profileService.getProfile(submission.profileId);
        }
    }

    private assignPriorities(submissions: ApplicationRequest[]) {
        submissions = submissions.sort(
            (a, b) => a.priority - b.priority
        );

        submissions.forEach((item, index) => {
            item.priority = index + 1;
        });
    }

    private groupApplicationsBySchool(applications: ApplicationWithProfiles[]) {
        const groupedBySchool: ApplicationBySchool[] = [];
        for (const app of applications) {
            let schoolGroup = groupedBySchool.find(group =>
                group.school.id === app.school.id
            );
            if (!schoolGroup) {
                schoolGroup = {
                    school: app.school,
                    profiles: [],
                };
                groupedBySchool.push(schoolGroup);
            }

            schoolGroup.profiles.push({
                profileId: app.profile.id,
                priority: app.priority,
            });
        }
        return groupedBySchool;
    };
}

import {CustomError} from "./customError";
import {ValidationError as ExpressValidationError} from "express-validator";

export class ValidationError extends CustomError {
