import {compare, hash} from 'bcrypt';

import {transactionFunction} from "../db";

import {UserRepository} from '../repositories/userRepository';
import {AuthenticationError} from "../errors/authenticationError";
import {DataConflictError} from "../errors/dataConflictError";
import {UserRequest} from "../dto/user/userRequest";
import {UserWithRoles} from "../dto/user/userWithRoles";
import {User} from "../dto/user/user";
import {UserEntity} from "../models/userEntity";

export class UserService {
    constructor(
        private userRepository: UserRepository,
        private readonly tx: transactionFunction
    ) {
    }

    /**
     *  Loguje użytkownika na podstawie loginu i hasła.
     *
     * @param {string} login - Login lub email użytkownika.
     * @param {string} password - Hasło użytkownika.
     * @returns {Promise<UserWithRoles>} Zwraca obiekt użytkownika razem z jego rolami.
     *
     * @throws {AuthenticationError} Jeśli login/email lub hasło są nieprawodłowe.
     */
    async login(login: string, password: string): Promise<UserWithRoles> {
        const existingUser: UserWithRoles | null = await this.userRepository.getWithRolesByLoginOrEmail(login, login);
        if (!existingUser || !(await compare(password, existingUser.password))) {
            throw new AuthenticationError('Nieprawidłowe email/nazwa użytkownika lub hasło');
        }

        return existingUser;
    }

    /**
     *  Rejestruje nowego użytkownika.
     *
     * @param {UserRequest} user - obiekt zawierający nazwę użytkownika, email oraz hasło.
     * @returns {Promise<void>}
     *
     * @throws {DataConflictError} Jeśli istnieje już użytkownik z podanym emailem lub nazwą użytkownika.
     */
    async register(user: UserRequest): Promise<void> {
        const existingUser: User | null = await this.userRepository.getWithoutRolesByLoginOrEmail(user.username, user.email);
        if (existingUser) {
            if (existingUser.username === user.username) {
                throw new DataConflictError('Już istnieje konto powiązane z tą nazwą użytkownika.');
            }
            if (existingUser.email === user.email) {
                throw new DataConflictError('Już istnieje konto powiązane z tym adresem email.');
            }
        }

        const hashedPassword: string = await hash(user.password, 12);
        const newUser: UserEntity = {
            id: 0,
            username: user.username,
            email: user.email,
            password: hashedPassword,
        };

        await this.tx(async t => {
            const user: UserEntity = await this.userRepository.insert(newUser, t);
            await this.userRepository.insertUserRoles(user.id, ['user'], t);
        });
    }

    /**
     *  Usuwa użytkownika.
     *
     * @param {number} id - identyfikator użytkownika.
     * @returns {Promise<void>}
     */
    async deleteUser(id: number): Promise<void> {
        await this.userRepository.deleteById(id)
    }
}

import {CustomError} from "./customError";

export class AuthenticationError extends CustomError {
    constructor(message: string = 'Authentication failed') {
        super(message, 401);
    }
}

export class CustomError extends Error {
    public statusCode: number;
    public message: string;
    public isOperational: boolean;

    constructor(message: string, statusCode: number, isOperational: boolean = true) {
        super(message);
        this.statusCode = statusCode;
        this.message = message;
        this.isOperational = isOperational;

        Error.captureStackTrace(this, this.constructor);
    }
}

import {CustomError} from "./customError";

export class DataConflictError extends CustomError {
    constructor(message: string = 'Data already exists') {
        super(message, 409);
    }
}

import {db, ITask} from '../db';
import {UserEntity} from "../models/userEntity";
import {UserEntityWithRoles} from "../models/userEntityWithRoles";

export class UserRepository {

    async insert(newUser: UserEntity, t: ITask<any>): Promise<UserEntity> {
        const userInsertQuery = `
            INSERT INTO users (username, email, password)
            VALUES ($1, $2, $3)
            RETURNING id;
        `;

        const values = [newUser.username, newUser.email, newUser.password];
        return t.one(userInsertQuery, values);
    }

    async insertUserRoles(userId: number, roles: string[], t: ITask<any>): Promise<void> {
        const query = `
            INSERT INTO user_roles (user_id, role_name)
            VALUES ($1, $2);
        `;

        for (const roleName of roles) {
            await t.none(query, [userId, roleName]);
        }
    }

    async getWithRolesByLoginOrEmail(username: string, email: string): Promise<UserEntityWithRoles | null> {
        const query = `
            SELECT u.*, array_agg(r.role_name) as roles
            FROM users u
                     LEFT JOIN user_roles r ON u.id = r.user_id
            WHERE u.username = $1
               or u.email = $2
            GROUP BY u.id
            LIMIT 1
        `;
        return await db.oneOrNone(query, [username, email]);
    }

    async getWithoutRolesByLoginOrEmail(username: string, email: string): Promise<UserEntity | null> {
        const query = `
            SELECT *
            FROM users
            WHERE username = $1
               OR email = $2
            LIMIT 1;
        `;

        return await db.oneOrNone(query, [username, email]);
    }

    async deleteById(id: number): Promise<void> {
        const query = `
            DELETE 
            FROM users
            WHERE id = $1
        `;

        await db.none(query, [id]);
    }
}

import pgPromise, {ITask} from 'pg-promise';
import dotenv from 'dotenv';

dotenv.config();

const pgp = pgPromise({
    receive(e) {
        camelizeColumns(e.data);
    }
});

function camelizeColumns(data: any[]) {
    const tmp = data[0];
    for (const prop in tmp) {
        const camel = pgp.utils.camelize(prop);
        if (!(camel in tmp)) {
            for (let i = 0; i < data.length; i++) {
                const d = data[i];
                d[camel] = d[prop];
                delete d[prop];
            }
        }
    }
}


const db = pgp({
    host: process.env.DB_HOST,
    port: process.env.DB_PORT ? parseInt(process.env.DB_PORT, 10) : 5432,
    database: process.env.DB_NAME,
    user: process.env.DB_USER,
    password: process.env.DB_PASSWORD,
});

type transactionFunction = (callback: (t: ITask<any>) => Promise<void>) => Promise<void>;

const tx: transactionFunction = async (callback) => {
    return db.tx(callback);
};

export {db, tx, transactionFunction, ITask};

import {ApplicationService} from "./applicationService";
import {ProfileService} from "./profileService";
import {ApplicationStatus} from "../dto/application/applicationStatus";
import {ApplicationRequest} from "../dto/application/applicationRequest";
import {transactionFunction} from "../db";
import {RankedApplication, RankListWithInfo} from "../dto/application/rankedApplication";
import {ApplicationWithInfo} from "../dto/application/applicationWithInfo";


export class AdminService {
    constructor(
        private profileService: ProfileService,
        private applicationService: ApplicationService,
        private readonly tx: transactionFunction
    ) {
    }

    async processProfileEnrollments(): Promise<ApplicationWithInfo[]> {
        const rankListsByProfile = await this.profileService.getAllRankLists();
        const finalEnrollmentLists = await this.finalizeEnrollmentProcess(rankListsByProfile);
        return await this.updateApplicationStatuses(finalEnrollmentLists)
    }

    private async finalizeEnrollmentProcess(rankListsByProfile: Map<number, RankListWithInfo>) {
        const applicationsByCandidate = this.toApplicationsByCandidate(rankListsByProfile)
        let backtrack = true;

        while (backtrack) {
            backtrack = false;
            for (const [, {accepted}] of rankListsByProfile) {
                backtrack = await this.processProfileApplications(accepted, applicationsByCandidate, rankListsByProfile);
                if (backtrack) break;
            }
        }
        return rankListsByProfile;
    }

    private toApplicationsByCandidate(rankedListsByProfile: Map<number, RankListWithInfo>) {
        const applicationsByCandidate = new Map<number, ApplicationRequest[]>();

        for (const [profileId, {accepted}] of rankedListsByProfile.entries()) {
            for (const application of accepted) {
                const candidateId = application.candidate.id;

                const applications = applicationsByCandidate.get(candidateId) || [];
                applications.push({profileId, priority: application.priority});

                applicationsByCandidate.set(candidateId, applications);
            }
        }
        return applicationsByCandidate;
    }

    private async processProfileApplications(
        accepted: RankedApplication[],
        applicationsByCandidate: Map<number, ApplicationRequest[]>,
        rankListsByProfile: Map<number, RankListWithInfo>
    ): Promise<boolean> {
        for (const application of accepted) {
            const candidateId = application.candidate.id;
            const candidateApplications = applicationsByCandidate.get(candidateId);
            if (!candidateApplications) continue;

            const highestPriority = this.getHighestPriority(candidateApplications);

            const removed = this.removeLowerPriorityApplications(
                candidateId,
                highestPriority,
                candidateApplications,
                rankListsByProfile,
                applicationsByCandidate
            );

            if (removed) {
                return true;
            }
        }
        return false;
    }

    private getHighestPriority(applications: ApplicationRequest[]) {
        return applications.reduce((highest, current) =>
            current.priority < highest.priority ? current : highest
        ).priority;
    }

    private removeLowerPriorityApplications(
        candidateId: number,
        highestPriority: number,
        candidateApplications: ApplicationRequest[],
        rankListsByProfile: Map<number, RankListWithInfo>,
        applicationsByCandidate: Map<number, ApplicationRequest[]>,
    ) {
        let removedFlag = false;

        for (const {profileId, priority} of candidateApplications) {
            if (priority === highestPriority) continue;

            const rankList = rankListsByProfile.get(profileId);
            const {accepted, reserve, rejected} = rankList || {accepted: [], reserve: [], rejected: []};

            const removed = this.removeApp(candidateId, profileId, accepted, rejected, applicationsByCandidate);
            if (!removed) continue;

            removedFlag = true;
            this.moveAppFromReserveToAccepted(profileId, reserve, accepted, applicationsByCandidate);
        }

        return removedFlag;
    }

    private removeApp(candidateId: number, profileId: number, accepted: RankedApplication[], rejected: RankedApplication[], applicationsByCandidate: Map<number, ApplicationRequest[]>) {
        const indexToRemove = accepted.findIndex(app => app.candidate.id === candidateId);
        if (indexToRemove === -1) return false;
        const removed = accepted.splice(indexToRemove, 1);
        rejected.push(removed[0]);

        const applications = applicationsByCandidate.get(candidateId) || [];
        applicationsByCandidate.set(candidateId, applications.filter(app => app.profileId !== profileId));

        return true;
    }

    private moveAppFromReserveToAccepted(profileId: number, reserve: RankedApplication[], accepted: RankedApplication[], applicationsByCandidate: Map<number, ApplicationRequest[]>) {
        const movedApp = reserve.shift();
        if (!movedApp) return

        accepted.push(movedApp);

        const candidateApps = applicationsByCandidate.get(movedApp.candidate.id) || [];
        candidateApps.push({profileId, priority: movedApp.priority});
        applicationsByCandidate.set(movedApp.candidate.id, candidateApps);
    }

    private async updateApplicationStatuses(finalEnrollmentLists: Map<number, RankListWithInfo>) {
        const updatedApplications: ApplicationWithInfo[] = []
        await this.tx(async t => {
            for (const [_profileId, rankLists] of finalEnrollmentLists.entries()) {
                const allApplications = [
                    ...rankLists.accepted.map(app => ({
                        ...app,
                        status: ApplicationStatus.Accepted,
                        profile: rankLists.profile
                    })),
                    ...rankLists.reserve.map(app => ({
                        ...app,
                        status: ApplicationStatus.Rejected,
                        profile: rankLists.profile
                    })),
                    ...rankLists.rejected.map(app => ({
                        ...app,
                        status: ApplicationStatus.Rejected,
                        profile: rankLists.profile
                    })),
                ];
                for (const a of allApplications) {
                    await this.applicationService.updateApplicationStatus(a.id, a.status, t);
                    updatedApplications.push(a);
                }
            }
        });
        return updatedApplications;
    }
}

export enum ApplicationStatus {
    Pending = 'Oczekujący',
    Accepted = 'Przyjęty',
    Rejected = 'Odrzucony',
}

import {ProfileRepository} from "../repositories/profileRepository";
import {ProfileCriteriaEntity, ProfileCriteriaType} from "../models/profileCriteriaEntity";
import {Profile} from "../dto/profile/profile";
import {ResourceNotFoundError} from "../errors/resourceNotFoundError";
import {ProfileCriteria} from "../dto/criteriaByProfile";
import {Grade} from "../dto/grade/grade";
import {ApplicationService} from "./applicationService";
import {GradeService} from "./gradeService";
import {Application} from "../dto/application/application";
import {ProfileRequest} from "../dto/profile/profileRequest";
import {transactionFunction} from "../db";
import {ProfileWithCriteria} from "../dto/profile/profileWithCriteria";
import {CandidateService} from "./candidateService";
import {RankedApplication, RankList, RankListWithInfo} from "../dto/application/rankedApplication";
import {GRADE_TO_POINTS} from "../../../adminConstants";
import {GradeType} from "../dto/grade/gradeType";
import {GradesInfo, PointsInfo} from "../dto/grade/pointsInfo";
import {SubjectService} from "./subjectService";
import {ProfileWithInfo} from "../dto/profile/profileInfo";
import {SchoolService} from "./schoolService";
import {DataConflictError} from "../errors/dataConflictError";

export class ProfileService {
    private applicationService!: ApplicationService;
    private schoolService!: SchoolService;

    constructor(
        private profileRepository: ProfileRepository,
        private gradeService: GradeService,
        private subjectService: SubjectService,
        private candidateService: CandidateService,
        private readonly tx: transactionFunction
    ) {
    }

    setApplicationService(applicationService: ApplicationService) {
        this.applicationService = applicationService;
    }

    setSchoolService(schoolService: SchoolService) {
        this.schoolService = schoolService;
    }

    /**
     *  Pobiera dane profilu na podstawie identyfikatora profilu.
     *
     * @param {number} id - identyfikator profilu.
     * @returns {Promise<Profile>} Zwraca obiekt profilu.
     *
     * @throws {ResourceNotFoundError} Jeśli nie znaleziono profilu o podanym identyfikatorze.
     */
    async getProfile(id: number): Promise<Profile> {
        const profile = await this.profileRepository.getById(id);
        if (!profile) throw new ResourceNotFoundError('Profil nie znaleziony.');
        return profile;
    }

    /**
     *  Pobiera dane wszystkich profili.
     *
     * @returns {Promise<Profile>} Zwraca tablicę obiektów profilu.
     */
    async getAllProfiles(): Promise<Profile[]> {
        return this.profileRepository.getAll();
    }

    /**
     *  Pobiera dane profilu wraz z jego kryteriami rekrutacyjnymi na podstawie identyfikatora profilu oraz identyfikatora szkoły do której należy profil.
     *
     * @param {number} id - identyfikator profilu.
     * @param {number} schoolId - identyfikator szkoły do której należy profil.
     * @returns {Promise<ProfileWithCriteria>} Zwraca obiekt profilu wraz z jego kryteriami rekrutacyjnymi.
     *
     * @throws {ResourceNotFoundError} Jeśli nie znaleziono profilu o podanym identyfikatorze
     * lub gdy profil o podanym identyfikatorze należy do innej szkoły, niż podanej.
     */
    async getProfileByIdAndSchoolId(id: number, schoolId: number): Promise<ProfileWithCriteria> {
        const profile = await this.profileRepository.getById(id);
        if (!profile || profile.schoolId != schoolId) throw new ResourceNotFoundError('Profil nie znaleziony.');

        return {
            ...profile,
            criteria: await this.getProfileCriteria(id)
        };
    }

    /**
     *  Pobiera dane pierwszego znalezionego w bazie danych profilu wraz z jego kryteriami rekrutacyjnymi na podstawie identyfikatora szkoły.
     *
     * @param {number} schoolId - identyfikator szkoły.
     * @returns {Promise<ProfileWithCriteria | null>} Zwraca obiekt profilu wraz z jego kryteriami rekrutacyjnymi, lub null, jeśli nie znaleziono profilu.
     */
    async getProfileBySchool(schoolId: number): Promise<ProfileWithCriteria | null> {
        const profile = await this.profileRepository.getBySchool(schoolId);
        if (!profile) return null;

        return {
            ...profile,
            criteria: await this.getProfileCriteria(profile.id)
        };
    }

    /**
     *  Pobiera dane wszystkich profili wraz z ich kryteriami rekrutacyjnymi na podstawie identyfikatora szkoły.
     *
     * @param {number} schoolId - identyfikator szkoły.
     * @returns {Promise<ProfileWithCriteria[]>} Zwraca tablicę obiektów profili wraz z ich kryteriami rekrutacyjnymi.
     */
    async getAllProfilesBySchool(schoolId: number): Promise<Profile[]> {
        return this.profileRepository.getAllBySchool(schoolId);
    }

    /**
     *  Pobiera dane wszystkich profili wraz z dodatkowymi informacjami, takimi jak:
     *
     *   - id: (number) - identyfikator profilu
     *   - name: (string) - nazwa profilu
     *   - schoolId: (number) - identyfikator szkoły do której należy profil
     *   - schoolName: (string) - nazwa szkoły do której należy profil
     *   - applicationNumber: (number) - ilość oczekujących aplikacji złożonych do tego profilu
     *   - criteriaSubjects: (string[]) - nazwy przedmiotów które należą do kryteriów rekrutacyjnych profilu
     *
     * @returns {Promise<ProfileWithInfo[]>} Zwraca tablicę obiektów profilu wraz z dodatkowymi informacjami.
     */
    async getProfilesWithInfo(): Promise<ProfileWithInfo[]> {
        const profiles = await this.getAllProfiles()
        return await Promise.all(profiles.map(async p => {
            const school = (await this.schoolService.getSchool(p.schoolId))

            const criteria = await this.getProfileCriteria(p.id);
            const criteriaSubjects = await Promise.all(criteria.map(async c => {
                const subject = await this.subjectService.getSubject(c.subjectId);
                return subject.name;
            }));
            const applicationNumber = (await this.applicationService.getAllPendingByProfile(p.id)).length;

            return {
                id: p.id,
                name: p.name,
                schoolId: school.id,
                schoolName: school.name,
                criteriaSubjects,
                applicationNumber
            }
        }));
    }

    /**
     *  Usuwa profil z bazy danych na podstawie podanego identyfikatora profilu oraz identyfikatora szkoły.
     *  Identyfikator szkoły służy do potwierdzenia czy jednostka szkolna ma uprawnienia do usunięcia tego profilu (tj. czy profil należy do tej szkoły).
     *
     * @param {number} id - identyfikator profilu.
     * @param {number} schoolId - identyfikator szkoły do której należy profil.
     * @returns {Promise<void>}
     *
     * @throws {ResourceNotFoundError} Jeśli nie znaleziono profilu o podanym identyfikatorze, lub znaleziony profil należy do innej szkoły.
     */
    async deleteProfile(id: number, schoolId: number): Promise<void> {
        const profile = await this.profileRepository.getById(id);
        if (!profile || profile.schoolId != schoolId) throw new ResourceNotFoundError("Nie znaleziono profilu do usunięcia.");
        await this.profileRepository.delete(id, schoolId);
    }

    /**
     *  Dodaje podany profil do bazy danych.
     *
     * @param {ProfileRequest} profile - Obiekt dodawanego profilu, który zawiera:
     *
     * - id: (number) - identyfikator profilu (wartość nie ma znaczenia, zostaje nadany nowy identyfikator na bazie danych).
     * - name: (string) - nazwa profilu
     * - capacity: (number) - liczba miejsc do rekrutacji w profilu
     * - criteria: (ProfileCriteria[]) - tablica obiektów kryteriów rekrutacyjnych dla profilu
     * @param {number} schoolId - identyfikator szkoły, do której dodajemy profil.
     * @returns {Promise<number>} identyfikator dodanego profilu nadany przez bazę danych.
     *
     * @throws {DataConflictError} Jeśli profil o podanej nazwie już istnieje dla danej szkoły.
     */
    async addProfile(profile: ProfileRequest, schoolId: number): Promise<number> {
        let insertedProfileId = 0;
        if (await this.profileRepository.getBySchoolAndName(schoolId, profile.name))
            throw new DataConflictError("Profil o podanej nazwie już istnieje.");

        const newProfile: Profile = {
            id: 0,
            name: profile.name,
            capacity: profile.capacity,
            schoolId: schoolId,
        }

        await this.tx(async t => {
            insertedProfileId = (await this.profileRepository.insert(newProfile, t)).id;

            for (const criteria of profile.criteria) {
                const newProfileCriteria: ProfileCriteriaEntity = {
                    id: 0,
                    profileId: insertedProfileId,
                    subjectId: criteria.subjectId,
                    type: criteria.type,
                }
                await this.profileRepository.insertCriteria(newProfileCriteria, t);
            }
        });
        return insertedProfileId;
    }

    /**
     *  Aktualizuje podany profil w bazie danych, jeśli należy on do podanej szkoły.
     *
     * @param {ProfileRequest} profile - Obiekt aktualizowanego profilu, który zawiera:
     *
     * - id: (number) - identyfikator profilu.
     * - name: (string) - nowa nazwa profilu
     * - capacity: (number) - nowa liczba miejsc do rekrutacji w profilu
     * - criteria: (ProfileCriteria[]) - tablica obiektów kryteriów rekrutacyjnych dla aktualizowanego profilu
     * @param {number} schoolId - identyfikator szkoły, do której należy profil.
     * @returns {Promise<void>}
     *
     * @throws {ResourceNotFoundError} Jeśli nie znaleziono profilu o podanym identyfikatorze lub profil należy do innej szkoły niż podaliśmy.
     */
    async updateProfile(profile: ProfileRequest, schoolId: number): Promise<void> {
        await this.getProfileByIdAndSchoolId(profile.id, schoolId);

        const updatedProfile: Profile = {
            id: profile.id,
            name: profile.name,
            capacity: profile.capacity,
            schoolId: schoolId,
        }

        await this.tx(async t => {
            await this.profileRepository.update(updatedProfile, t);
            await this.profileRepository.deleteCriteriaByProfile(updatedProfile.id, t);

            for (const criteria of profile.criteria) {
                const newProfileCriteria: ProfileCriteriaEntity = {
                    id: 0,
                    profileId: updatedProfile.id,
                    subjectId: criteria.subjectId,
                    type: criteria.type,
                }
                await this.profileRepository.insertCriteria(newProfileCriteria, t);
            }
        });
    }

    /**
     *  Pobiera informację o punktach, które zdobył podany kandydat do profilu.
     *
     * @param {number} id - identyfikator profilu.
     * @param {number} candidateId - identyfikator kandydata kandydującego do tego profilu.
     * @returns {Promise<PointsInfo>} Zwraca obiekt informacji o punktach, który zawiera:
     *
     * - points: (number) - ilość zdobytych punktów do profilu, obliczona w oparci o oceny kandydata oraz kryteria profilu.
     * - gradesInfo: (GradesInfo) - obiekt zawierający informacje o ocenach ze świadectwa kandydata:
     *
     *      - grade- wartość oceny
     *      - subject- przedmiot, z którego jest ocena
     *      - type- typ kryterium rekrutacyjnego jakie spełnia przedmiot dla danego profilu (czy jest to kryterium obowiązkowe, czy alternatywne)
     *
     * @throws {ResourceNotFoundError} Jeśli nie znaleziono kryteriów dla podanego profilu.
     */
    async getPoints(id: number, candidateId: number): Promise<PointsInfo> {
        const criteria = await this.getProfileCriteria(id);
        const grades: Grade[] = await this.gradeService.getAllByCandidate(candidateId);

        const gradesInfo: GradesInfo[] = (await Promise.all(grades
            .map((async g => {
                const criterion = criteria.find(c => c.subjectId == g.subjectId && g.type === GradeType.Certificate)
                return criterion ? {
                    grade: g.grade,
                    subject: (await this.subjectService.getSubject(g.subjectId)).name,
                    type: criterion.type
                } : null
            })))).filter(g => g !== null)

        return {points: this.calculatePoints(criteria, grades), gradesInfo};
    }

    /**
     *  Pobiera listę rekrutacyjną dla podanego profilu.
     *  Listy rekrutacyjne są sortowane według punktów uzyskanych przez kandydatów.
     *
     * @param {number} id - identyfikator profilu.
     * @returns {Promise<RankList>} Zwraca listę rekrutacyjną, na którą składa się:
     *
     *  - prevAccepted: lista kandydatów przyjętych w poprzednich turach
     *  - accepted: lista kandydatów kwalifikujących się w obecnej turze
     *  - reserve: lista kandydatów niekwalifikujących się, którzy są na liście rezerwowej
     *
     * @throws {ResourceNotFoundError} Jeśli nie znaleziono kryteriów dla profilu.
     */
    async getRankList(id: number): Promise<RankList> {
        const criteria = await this.getProfileCriteria(id);
        const accepted = await this.applicationService.getAllAcceptedByProfile(id);
        const pending = await this.applicationService.getAllPendingByProfile(id);

        const rankedAccepted = await this.createRankList(accepted, criteria);
        const rankedPending = await this.createRankList(pending, criteria);

        const profileCapacity = await this.getProfileCapacity(id);
        const capacity = profileCapacity - rankedAccepted.length;
        return {
            prevAccepted: rankedAccepted,
            accepted: rankedPending.slice(0, capacity),
            reserve: rankedPending.slice(capacity)
        };
    }

    /**
     *  Pobiera wszystkie listy rekrutacyjne dla każdego profilu wraz z dodatkowymi informacjami.
     *
     * @returns {Promise<Map<number, RankListWithInfo>>} Zwraca mapę, dla której:
     *
     * klucz - identyfikator profilu
     * wartość - lista rankingowa, która zawiera:
     *
     *   - profile - profil z dodatkowymi informacjami
     *   - accepted - listę zaakceptowanych kandydatów
     *   - reserve - listę rezerwową kandydatów
     *   - rejected - listę odrzuconych, początkowo ustawioną na pustą tablicę
     *
     * @throws {ResourceNotFoundError} Jeśli nie znaleziono kryteriów dla profilu.
     */
    async getAllRankLists(): Promise<Map<number, RankListWithInfo>> {
        const profiles = await this.getProfilesWithInfo();
        const rankLists = new Map<number, RankListWithInfo>();

        for (const profile of profiles) {
            const {accepted, reserve} = await this.getRankList(profile.id);
            rankLists.set(profile.id, {profile, accepted, reserve, rejected: []});
        }

        return rankLists;
    }

    private async createRankList(applications: Application[], criteria: ProfileCriteria[]) {
        const rankedApplications = await Promise.all(
            applications.map(a => this.toRankedApplication(criteria, a))
        );
        return rankedApplications.sort((a, b) => b.points - a.points);
    }

    private async toRankedApplication(criteria: ProfileCriteria[], application: Application): Promise<RankedApplication> {
        const candidate = await this.candidateService.getCandidateById(application.candidateId);
        const grades = await this.gradeService.getAllByCandidate(application.candidateId);

        const points = this.calculatePoints(criteria, grades);

        return {
            id: application.id,
            candidate,
            points,
            priority: application.priority,
            status: application.status
        }
    }

    private async getProfileCapacity(profileId: number) {
        const capacity = await this.profileRepository.getProfileCapacity(profileId);
        if (!capacity) throw new ResourceNotFoundError('Nie znaleziono liczby miejsc dla profilu.');
        return capacity
    }

    private async getProfileCriteria(profileId: number): Promise<ProfileCriteria[]> {
        const criteria: ProfileCriteriaEntity[] = await this.profileRepository.getProfileCriteria(profileId);
        if (!criteria) throw new ResourceNotFoundError('Nie znaleziono kryteriów dla profilu.');
        return criteria;
    }

    private calculatePoints(profileCriteria: ProfileCriteriaEntity[], grades: Grade[]): number {
        const mandatorySubjects = profileCriteria.filter(s => s.type === ProfileCriteriaType.Mandatory);
        const alternativeSubjects = profileCriteria.filter(s => s.type === ProfileCriteriaType.Alternative);
        let points = 0;

        let alternativeGrades = []
        for (let grade of grades) {
            if (grade.type == GradeType.Exam) {
                points += grade.grade * 0.35;
            } else if (mandatorySubjects.some(s => s.subjectId == grade.subjectId)) {
                points += GRADE_TO_POINTS[grade.grade] || 0;
            } else if (alternativeSubjects.some(s => s.subjectId == grade.subjectId)) {
                alternativeGrades.push(GRADE_TO_POINTS[grade.grade] || 0);
            }
        }
        if (alternativeGrades.length > 0) {
            points += Math.max(...alternativeGrades);
        }
        return Math.ceil(points * 100) / 100;
    }
}

export enum ProfileCriteriaType {
    Mandatory = 'mandatory',
    Alternative = 'alternative',
}

export interface ProfileCriteriaEntity {
    id: number;
    profileId: number;
    subjectId: number;
    type: ProfileCriteriaType;
}

import {CustomError} from "./customError";

export class ResourceNotFoundError extends CustomError {
    constructor(message: string = 'Data does not exist') {
        super(message, 404);
    }
}

export enum GradeType {
    Exam = 'exam',
    Certificate = 'certificate',
}

import {ITask} from "pg-promise";

import {ApplicationRepository} from '../repositories/applicationRepository';
import {ResourceNotFoundError} from "../errors/resourceNotFoundError";
import {DataConflictError} from "../errors/dataConflictError";
import {SchoolService} from "./schoolService";
import {ProfileService} from "./profileService";
import {ApplicationWithProfiles} from "../dto/application/applicationWithProfiles";
import {ApplicationBySchool} from "../dto/application/applicationBySchool";
import {ApplicationRequest} from "../dto/application/applicationRequest";
import {ApplicationEntity} from "../models/applicationEntity";
import {Application} from "../dto/application/application";
import {EnrollmentService} from "./enrollmentService";
import {ValidationError} from "../errors/validationError";
import {Enrollment} from "../dto/enrollment";
import {ApplicationStatus} from "../dto/application/applicationStatus";
import {transactionFunction} from "../db";
import {SCHOOL_MAX} from "../../../adminConstants";

export class ApplicationService {
    private profileService!: ProfileService;

    constructor(
        private applicationRepository: ApplicationRepository,
        private enrollmentService: EnrollmentService,
        private schoolService: SchoolService,
        private readonly tx: transactionFunction
    ) {
    }

    setProfileService(profileService: ProfileService) {
        this.profileService = profileService;
    }

    /**
     * Pobiera wszystkie aplikacje wraz z profilami złożone przez podanego kandydata
     *
     * @param {number} candidateId - identyfikator kandydata.
     * @returns {Promise<School>} Zwraca tablicę obiektów aplikacji z profilami. Obiekty te zawierają:
     *
     *  id: number - identyfikator aplikacji
     *  round: number - turę naboru
     *  school: SchoolWithProfiles - obiekt szkoły do której złożono aplikację wraz ze wszystkimi profilami szkoły
     *  profile: Profile - obiekt profilu do którego złożono aplikację
     *  priority: number - priorytet
     *  status: string - status aplikacji
     *  createdAt: Date | number - data utworzenia aplikacji
     *  updatedAt: Date | number - data modyfikacji aplikacji
     */
    async getAllApplications(candidateId: number): Promise<ApplicationWithProfiles[]> {
        const applications = await this.applicationRepository.getAllByCandidate(candidateId);

        return Promise.all(
            applications.map(async (app) => {
                const [enrollment, profile] = await Promise.all([
                    this.enrollmentService.getEnrollment(app.enrollmentId),
                    this.profileService.getProfile(app.profileId),
                ]);
                const school = await this.schoolService.getSchoolWithProfiles(profile.schoolId)

                return {
                    id: app.id,
                    school: school,
                    profile: profile,
                    priority: app.priority,
                    round: enrollment.round,
                    status: app.status,
                    createdAt: app.createdAt,
                    updatedAt: app.updatedAt
                };
            })
        )
    }

    /**
     * Pobiera wszystkie aplikacje ze statusem oczekującym dla podanego profilu
     *
     * @param {number} profileId - identyfikator profilu.
     * @returns {Promise<Application[]>} Zwraca tablicę obiektów oczekujących aplikacji. Obiekty te zawierają:
     *
     *  id: number - identyfikator aplikacji
     *  profileId: number - identyfikator profilu
     *  candidateId: number - identyfikator kandydata
     *  priority: number - priorytet
     *  status: string - status aplikacji
     */
    async getAllPendingByProfile(profileId: number): Promise<Application[]> {
        return this.applicationRepository.getAllByProfileAndStatus(profileId, ApplicationStatus.Pending);
    }

    /**
     * Pobiera wszystkie aplikacje ze statusem zaakceptowanym dla podanego profilu
     *
     * @param {number} profileId - identyfikator profilu.
     * @returns {Promise<Application[]>} Zwraca tablicę obiektów zaakceptowanych aplikacji. Obiekty te zawierają:
     *
     *  id: number - identyfikator aplikacji
     *  profileId: number - identyfikator profilu
     *  candidateId: number - identyfikator kandydata
     *  priority: number - priorytet
     *  status: string - status aplikacji
     */
    async getAllAcceptedByProfile(profileId: number): Promise<Application[]> {
        return this.applicationRepository.getAllByProfileAndStatus(profileId, ApplicationStatus.Accepted)
    }

    /**
     * Pobiera wszystkie aplikacje złożone przez podanego kandydata, pogrupowane ze względu na szkoły.
     * Zatem wszystkie profile należące do danej szkoły, do których złożył aplikację kandydat, będą w jednej grupie.
     *
     * @param {number} candidateId - identyfikator kandydata.
     * @returns {Promise<ApplicationBySchool[]>} Zwraca tablicę obiektów pogrupowanych aplikacji. Obiekty te zawierają:
     *
     *  - school: SchoolWithProfiles - szkołę wraz ze wszystkimi profilami należącymi do tej szkoły
     *  - profiles:  ApplicationRequest[]:
     *
     *     - profileId: number - identyfikator profilu
     *     - priority: number - priorytet
     */
    async getAllApplicationSubmissions(candidateId: number): Promise<ApplicationBySchool[]> {
        const applications: ApplicationWithProfiles[] = await this.getAllApplications(candidateId);
        return this.groupApplicationsBySchool(applications);
    }

    /**
     * Aktualizuje status podanej aplikacji, na ten, podany w argumencie.
     *
     * @param {number} id - identyfikator aplikacji.
     * @param {ApplicationStatus} status - status aplikacji ("Oczekujący" | "Przyjęty" | "Odrzucony"
     * @param {ITask<any>} t - obiekt transakcji
     * @returns {Promise<void>}
     */
    async updateApplicationStatus(id: number, status: ApplicationStatus, t: ITask<any>): Promise<void> {
        return this.applicationRepository.updateStatus(id, status, t);
    }


    /**
     * Dodaje nowe aplikacje złożone przez podanego kandydata.
     *
     * @param {number} candidateId - identyfikator kandydata składającego aplikacje.
     * @param {ApplicationRequest[]} submissions - tablica  obiektów aplikacji, które zawierają:
     *
     *  - profileId: number - identyfikator profilu
     *  - priority: number - priorytet aplikacji
     * @returns {Promise<void>}
     *
     * @throws {ValidationError} Jeśli kandydat próbuje złożyć aplikację poza okresem naboru
     * @throws {DataConflictError} Jeśli kandydat już wcześniej złożył aplikację
     */
    async addApplication(submissions: ApplicationRequest[], candidateId: number): Promise<void> {
        const enrollment: Enrollment | null = await this.enrollmentService.getCurrentEnrollment();
        if (!enrollment) throw new ValidationError('Nie można złożyć aplikacji poza okresem naboru.');

        const existingApplications: Application[] = await this.applicationRepository.getAllByCandidateAndEnrollmentId(candidateId, enrollment.id);
        if (existingApplications.length > 0) throw new DataConflictError('Aplikacja już istnieje.');

        await this.validateProfiles(submissions);
        this.assignPriorities(submissions);

        await this.tx(async t => {
            for (const submission of submissions) {
                const newApplication = this.createApplicationEntity(submission, candidateId, enrollment.id);
                await this.applicationRepository.insert(newApplication, t);
            }
        });
    }

    /**
     * Aktualizuje aplikacje złożone przez podanego kandydata.
     *
     * @param {number} candidateId - identyfikator kandydata edytującego aplikacje.
     * @param {ApplicationRequest[]} submissions - tablica  obiektów aplikacji, które zawierają:
     *
     *  - profileId: number - identyfikator profilu
     *  - priority: number - priorytet aplikacji
     * @returns {Promise<void>}
     *
     * @throws {ValidationError} Jeśli kandydat próbuje edytować aplikację poza okresem naboru
     * @throws {ResourceNotFoundError} Jeśli nie znaleziono aplikacji
     */
    async updateApplication(submissions: ApplicationRequest[], candidateId: number): Promise<void> {
        const enrollment: Enrollment | null = await this.enrollmentService.getCurrentEnrollment();
        if (!enrollment) throw new ValidationError('Nie można złożyć aplikacji poza okresem naboru.');

        const applications: Application[] = await this.applicationRepository.getAllByCandidateAndEnrollmentId(candidateId, enrollment.id);
        if (applications.length === 0) throw new ResourceNotFoundError('Nie odnaleziono aplikacji.');

        await this.validateProfiles(submissions);
        this.assignPriorities(submissions);

        await this.tx(async t => {
            for (const application of applications) {
                await this.applicationRepository.delete(application.profileId, application.candidateId, t);
            }
            for (const submission of submissions) {
                const newApplication = this.createApplicationEntity(submission, candidateId, enrollment.id);
                await this.applicationRepository.insert(newApplication, t);
            }
        });
    }

    /**
     * Aktualizuje status aplikacji o podanym identyfikatorze na odrzucony. Wymaga profilu,
     * aby uwierzytelnić że aktualizacje przeprowadza administrator szkolny, który ma dostęp do zarządzania tą aplikacją
     *
     * @param {number} id - identyfikator aplikacji.
     * @param {number} profileId - identyfikator profilu.
     * @returns {Promise<void>}
     *
     * @throws {ResourceNotFoundError} Jeśli nie znaleziono aplikacji
     */
    async rejectApplication(id: number, profileId: number): Promise<void> {
        const application = await this.applicationRepository.getById(id);
        if (!application || profileId != application.profileId) throw new ResourceNotFoundError("Nie znaleziono aplikacji do usunięcia.");
        await this.applicationRepository.rejectById(id);
    }

    private createApplicationEntity(submission: ApplicationRequest, candidateId: number, enrollmentId: number): ApplicationEntity {
        return {
            id: 0,
            candidateId,
            profileId: submission.profileId,
            priority: submission.priority,
            enrollmentId,
            status: ApplicationStatus.Pending,
            createdAt: new Date(),
            updatedAt: new Date(),
        };
    }

    private async validateProfiles(submissions: ApplicationRequest[]) {
        const schoolIds = new Set<number>();

        for (const submission of submissions) {
            const profile = await this.profileService.getProfile(submission.profileId);
            if (profile && profile.schoolId) {
                schoolIds.add(profile.schoolId);
            }
        }

        if (schoolIds.size > SCHOOL_MAX) throw new ValidationError("Przekroczono dopuszczalną liczbę szkół");
    }

    private assignPriorities(submissions: ApplicationRequest[]) {
        submissions = submissions.sort(
            (a, b) => a.priority - b.priority
        );

        submissions.forEach((item, index) => {
            item.priority = index + 1;
        });
    }

    private groupApplicationsBySchool(applications: ApplicationWithProfiles[]) {
        const groupedBySchool: ApplicationBySchool[] = [];
        for (const app of applications) {
            let schoolGroup = groupedBySchool.find(group =>
                group.school.id === app.school.id
            );
            if (!schoolGroup) {
                schoolGroup = {
                    school: app.school,
                    profiles: [],
                };
                groupedBySchool.push(schoolGroup);
            }

            schoolGroup.profiles.push({
                profileId: app.profile.id,
                priority: app.priority,
            });
        }
        return groupedBySchool;
    };
}

import {CustomError} from "./customError";
import {ValidationError as ExpressValidationError} from "express-validator";

export class ValidationError extends CustomError {
    public errors: ExpressValidationError[];

    constructor(message: string, errors: ExpressValidationError[] = []) {
        super(message, 400);
        this.errors = errors;
    }
}

import {db, ITask} from '../db';
import {ApplicationEntity} from "../models/applicationEntity";
import {ApplicationStatus} from "../dto/application/applicationStatus";

export class ApplicationRepository {

    async getAllByCandidateAndEnrollmentId(candidateId: number, enrollmentId: number): Promise<ApplicationEntity[]> {
        const query = `
            SELECT a.candidate_id as candidate_id,
                   p.id           AS profile_id,
                   s.id           AS school_id,
                   a.priority,
                   a.status,
                   a.enrollment_id,
                   a.created_at,
                   a.updated_at
            FROM applications a
                     JOIN profiles p ON a.profile_id = p.id
                     JOIN schools s ON p.school_id = s.id
            WHERE candidate_id = $1
              and enrollment_id = $2
        `;

        return await db.query(query, [candidateId, enrollmentId]);
    }

    async getAllByCandidate(candidateId: number): Promise<ApplicationEntity[]> {
        const query = `
            SELECT a.id,
                   a.candidate_id,
                   p.id AS profile_id,
                   s.id AS school_id,
                   a.priority,
                   a.status,
                   a.enrollment_id,
                   a.created_at,
                   a.updated_at
            FROM applications a
                     JOIN profiles p ON a.profile_id = p.id
                     JOIN schools s ON p.school_id = s.id
            WHERE candidate_id = $1
            ORDER BY date_part('year', a.updated_at) DESC, a.priority
        `;

        return await db.query(query, [candidateId]);
    }

    async updateStatus(id: number, status: string, t: ITask<any>): Promise<void> {
        const query = `
            UPDATE applications
            SET status = $2
            WHERE id = $1
        `;

        await t.none(query, [id, status]);
    }

    async getById(id: number): Promise<ApplicationEntity | null> {
        const query = `
            SELECT *
            FROM applications
            WHERE id = $1
            LIMIT 1;
        `;

        return await db.oneOrNone(query, [id]);
    }

    async rejectById(id: number): Promise<void> {
        const query = `
            UPDATE applications
            SET status = $2
            WHERE id = $1
        `;

        await db.none(query, [id, ApplicationStatus.Rejected]);
    }

    async getAllByProfileAndStatus(profileId: number, status: ApplicationStatus): Promise<ApplicationEntity[]> {
        const query = `
            SELECT *
            FROM applications
            WHERE profile_id = $1
              and status = $2
        `;

        return db.query(query, [profileId, status]);
    }

    async insert(newApp: ApplicationEntity, t: ITask<any>): Promise<void> {
        const query = `
            INSERT INTO applications (candidate_id, profile_id, priority, enrollment_id, status)
            VALUES ($1, $2, $3, $4, $5)
        `;

        await t.none(
            query,
            [newApp.candidateId, newApp.profileId, newApp.priority, newApp.enrollmentId, newApp.status]
        );
    }

    async delete(profileId: number, candidateId: number, t: ITask<any>): Promise<void> {
        const query = `
            DELETE
            FROM applications
            WHERE profile_id = $1
              AND candidate_id = $2
        `;

        await t.none(query, [profileId, candidateId]);
    }
}

import {SchoolRepository} from "../repositories/schoolRepository";
import {ProfileService} from "./profileService";
import {SchoolWithProfiles} from "../dto/school/schoolWithProfiles";
import {ResourceNotFoundError} from "../errors/resourceNotFoundError";
import {School} from "../dto/school/school";
import {transactionFunction} from "../db";

export class SchoolService {
    constructor(
        private schoolRepository: SchoolRepository,
        private profileService: ProfileService,
        private readonly tx: transactionFunction
    ) {
    }

    /**
     * Pobiera dane szkoły na podstawie identyfikatora
     *
     * @param {number} id - identyfikator szkoły.
     * @returns {Promise<School>} Zwraca obiekt szkoły.
     *
     * @throws {ResourceNotFoundError} Jeśli nie znaleziono szkoły o podanym identyfikatorze.
     */
    async getSchool(id: number): Promise<School> {
        const school = await this.schoolRepository.getById(id);
        if (!school) throw new ResourceNotFoundError("Nie znaleziono szkoły")
        return school;
    }

    /**
     *  Pobiera dane wszystkich szkół.
     *
     * @returns {Promise<School[]>} Zwraca tablicę obiektów szkół.
     */
    async getAllSchools(): Promise<School[]> {
        return await this.schoolRepository.getAll();
    }

    /**
     *  Pobiera dane szkoły razem z powiązanymi profilami na podstawie identyfikatora szkoły.
     *
     * @param {number} id - identyfikator szkoły.
     * @returns {Promise<SchoolWithProfiles>} Zwraca obiekt szkoły razem z profilami.
     *
     * @throws {ResourceNotFoundError} Jeśli nie znaleziono szkoły o podanym identyfikatorze.
     */
    async getSchoolWithProfiles(id: number): Promise<SchoolWithProfiles> {
        const school: School | null = await this.schoolRepository.getById(id);

        if (!school) throw new ResourceNotFoundError('Szkoła nie znaleziona');

        return {
            id: school.id,
            name: school.name,
            profiles: await this.profileService.getAllProfilesBySchool(school.id),
        }
    }

    /**
     *  Pobiera dane wszystkich szkół razem z powiązanymi profilami.
     *
     * @returns {Promise<SchoolWithProfiles[]>} Zwraca tablicę obiektów szkół razem z ich profilami.
     */
    async getAllSchoolsWithProfiles(): Promise<SchoolWithProfiles[]> {
        let schools: School[] = await this.schoolRepository.getAll();
        let schoolsWithProfiles: SchoolWithProfiles[] = [];
        for (let school of schools) {
            schoolsWithProfiles.push({
                id: school.id,
                name: school.name,
                profiles: await this.profileService.getAllProfilesBySchool(school.id),
            });
        }
        return schoolsWithProfiles
    }

    /**
     *  Pobiera dane szkoły na podstawie identyfikatora szkoły oraz identyfikatora administratora tej szkoły.
     *
     * @param {number} id - identyfikator szkoły.
     * @param {number} adminId - identyfikator administratora szkolnego zarządzającego tą szkołą.
     * @returns {Promise<School>} Zwraca obiekt szkoły.
     *
     * @throws {ResourceNotFoundError} Jeśli nie znaleziono szkoły o podanym identyfikatorze i administratorze.
     */
    async getSchoolByAdminAndId(id: number, adminId: number): Promise<School> {
        const school: School | null = await this.schoolRepository.getByIdAndAdmin(id, adminId);
        if (school == null) throw new ResourceNotFoundError('Szkoła nie znaleziona');
        return {
            id: school.id,
            name: school.name,
        }
    }

    /**
     *  Pobiera dane pierwszej szkoły z bazy danych, która jest zarządzana przez podanego administratora szkolnego.
     *
     * @param {number} adminId - id administratora szkolnego.
     * @returns {Promise<School | null>} Zwraca obiekt szkoły, jeśli istnieje szkoła zarządzana przez podanego administratora, lub null wpp.
     */
    async getSchoolByAdmin(adminId: number): Promise<School | null> {
        return this.schoolRepository.getFirstByAdmin(adminId);
    }

    /**
     *  Pobiera dane wszystkich szkół zarzadzanych przez podanego admministratora szkolnego.
     *
     * @param {number} adminId - id administratora szkolnego.
     * @returns {Promise<School[]>} Zwraca tablicę obiektów szkół zarządzanych przez podanego administratora.
     */
    async getSchoolsByAdmin(adminId: number): Promise<School[]> {
        return await this.schoolRepository.getAllByAdmin(adminId);
    }

    /**
     *  Dodaje podaną szkołę do bazy danych:
     *
     * @param {School} school - obiekt szkoły zawierający:
     *
     *  - `id` (number) - identyfikator szkoły (zostanie zastąpiony przez ten, wygenerowany w bazie danych)
     *  - `name` (string) nowa nazwa szkoły
     * @returns {Promise<School>} Szkołę wraz z identyfikatorem nadanym przez bazę danych
     */
    async addSchool(school: School): Promise<School> {
        let result: School | null = null;
        await this.tx(async t => {
            result = await this.schoolRepository.insert(school, t)
        });
        return result!;
    }

    /**
     *  Usuwa szkołę o podanym id:
     *
     * @param {number} id - identyfikator szkoły
     *
     * @returns {Promise<void>}
     */
    async deleteSchool(id: number): Promise<void> {
        await this.tx(async t => {
            await this.schoolRepository.delete(id, t)
        });
    }

    /**
     *  Aktualizuje podane szkoły poprzez:
     *  - dodanie szkół, których id nie jest obecne w bazie danych
     *  - usunięcie szkół, których nie ma w podanych szkołach, a które są obecne w bazie danych
     *  - zaktualizowanie szkół których id jest obecne w podanych szkołach oraz w bazie danych.
     *
     * @param {School[]} schools - tablica obiektów szkół do aktualizowania.
     *
     * Każdy obiekt zawiera:
     *
     *  - `id` (number) - identyfikator szkoły
     *  - `name` (string) nowa nazwa szkoły
     * @returns {Promise<void>}
     */
    async updateSchools(schools: School[]): Promise<void> {
        const currentSchools = await this.getAllSchools();
        const currentSchoolIds = currentSchools.map(s => s.id);
        const newSchoolIds = schools.map(s => s.id);

        const schoolsToUpdate = schools.filter(s =>
            currentSchoolIds.includes(s.id)
        );
        const schoolsToDelete = currentSchools.filter(s =>
            !newSchoolIds.includes(s.id)
        );
        const schoolsToAdd = schools.filter(s =>
            !currentSchoolIds.includes(s.id)
        );

        await this.tx(async t => {
            for (const school of schoolsToDelete) {
                await this.schoolRepository.delete(school.id, t);
            }
            for (const school of schoolsToUpdate) {
                await this.schoolRepository.update(school, t);
            }
            for (const school of schoolsToAdd) {
                await this.schoolRepository.insert(school, t);
            }
        });
    }
}

