        const query = `
            INSERT INTO enrollments (round, start_date, end_date)
            VALUES ($1, $2, $3)
        `;
        const values = [newEnrollment.round, newEnrollment.startDate, newEnrollment.endDate];

        await t.none(query, values);
    }

