            DELETE
            FROM profile_criteria
            WHERE profile_id = $1
        `;

        await t.none(query, [profileId]);
    }

    async delete(profileId: number, schoolId: number): Promise<void> {
        const query = `
            DELETE
            FROM profiles
            WHERE id = $1
              AND school_Id = $2
        `;

        await db.none(query, [profileId, schoolId]);
    }
}

import {db, ITask} from '../db';
import {SchoolEntity} from "../models/schoolEntity";

export class SchoolRepository {
    async getById(id: number): Promise<SchoolEntity | null> {
        const query = `
            SELECT *
            FROM schools
            WHERE id = $1
            LIMIT 1
        `;

        return await db.oneOrNone(query, [id]);
    }

    async getAll(): Promise<SchoolEntity[]> {
        const query = `
            SELECT id, name
            FROM schools;
        `;

        return await db.query<SchoolEntity[]>(query);
    }

    async insert(newSchool: SchoolEntity, t: ITask<any>): Promise<SchoolEntity> {
        const query = `
            INSERT INTO schools (name)
            VALUES ($1)
            RETURNING *
        `;
        const values = [newSchool.name];

        return await t.one(query, values);
    }

    async update(school: SchoolEntity, t: ITask<any>): Promise<void> {
        const query = `
            UPDATE schools
            SET name = $2
            WHERE id = $1
        `;
        const values = [school.id, school.name];

        await t.none(query, values);
    }

    async delete(id: number, t: ITask<any>): Promise<void> {
        const query = `
            DELETE
            FROM schools
            WHERE id = $1
        `;
        const values = [id];

        await t.none(query, values);
    }

    async getByIdAndAdmin(id: number, adminId: number) {
        const query = `
            SELECT s.id,
                   s.name
            FROM schools s
                     JOIN school_admins a ON s.id = a.school_id
            WHERE a.school_id = $1
              AND a.user_id = $2
            LIMIT 1;
        `;

        return await db.oneOrNone<SchoolEntity>(query, [id, adminId]);
    }

    async getFirstByAdmin(id: number) {
        const query = `
            SELECT
                s.id,
                s.name
            FROM schools s
                     JOIN school_admins a ON s.id = a.school_id
            WHERE a.user_id = $1
            LIMIT 1;
        `;

        return await db.oneOrNone<SchoolEntity>(query, id);
    }

    async getAllByAdmin(id: number) {
        const query = `
            SELECT
                s.id,
                s.name
            FROM schools s
            JOIN school_admins a ON s.id = a.school_id
            WHERE a.user_id = $1
        `;

        return await db.query<SchoolEntity[]>(query, [id]);
    }
}

